import {Component, OnInit} from '@angular/core';
import {VolunteersService} from "../services/volunteers.service";
import {PetsService} from "../services/pets.service";
import {SheltersService} from "../services/shelters.service";

@Component({
    selector: 'app-welcome-page',
    templateUrl: './welcome-page.component.html',
    styleUrls: ['./welcome-page.component.css']
})

export class WelcomePageComponent implements OnInit{
  number_of_volunteers: number | string;
  number_of_pets: number | string;
  number_of_shelters: number | string;
  loading_error: boolean;

  constructor(
    private volunteersService: VolunteersService,
    private petsService: PetsService,
    private sheltersService: SheltersService
  ) {
    this.loading_error = false;
    this.number_of_pets = '?';
    this.number_of_volunteers = '?';
    this.number_of_shelters = '?';
  }
  ngOnInit(): void {
    this.petsService.getAllPets().subscribe((pets) => {
      this.number_of_pets = pets.length;
    }, err => {
      this.loading_error = true;
    });

    this.volunteersService.getAllVolunteers().subscribe((volunteers) => {
      this.number_of_volunteers = volunteers.length;
    }, err => {
      this.loading_error = true;
    });

    this.sheltersService.getAllShelters().subscribe((shelters) => {
      this.number_of_shelters = shelters.length;
    }, err =>{
      this.loading_error = true;
    });
  }

}
<h2 align="center">Welcome to Adopt Me</h2>

<h6>
  We have <span class="{{loading_error ? 'text-danger' : 'text-info'}}">{{number_of_shelters}}</span> shelters,
  <span class="{{loading_error ? 'text-danger' : 'text-info'}}">{{number_of_pets}}</span> pets and
  <span class="{{loading_error ? 'text-danger' : 'text-info'}}">{{number_of_volunteers}}</span> volunteers
</h6>

<div id="myCarousel" class="carousel slide" data-bs-ride="carousel">
  <div class="carousel-indicators">
    <button type="button" data-bs-target="#myCarousel" data-bs-slide-to="0" class="active" aria-current="true" aria-label="Slide 1"></button>
    <button type="button" data-bs-target="#myCarousel" data-bs-slide-to="1" aria-label="Slide 2"></button>
    <button type="button" data-bs-target="#myCarousel" data-bs-slide-to="2" aria-label="Slide 3"></button>
  </div>
  <div class="carousel-inner">
    <div class="carousel-item active">
      <svg class="bd-placeholder-img" width="100%" height="100%" xmlns="http://www.w3.org/2000/svg" aria-hidden="true" preserveAspectRatio="xMidYMid slice" focusable="false"><rect width="100%" height="100%" fill="var(--bs-secondary-color)"/></svg>
      <div class="container">
        <div class="carousel-caption text-start">
          <h1>Huge selection of pets</h1>
          <p>Проблема численности бездомных животных – это проблема не животных, а людей! Всем известно, что бездомные животные могут стать источником опасных заболеваний (например, бешенства). Также не является секретом то, что службы, занимающиеся отловом и истреблением бездомных животных, абсолютно не влияют на их численность. То есть, их работа – неэффективна! Количество бездомных животных не просто не уменьшается, а увеличивается!</p>
          <p><a class="btn btn-lg btn-primary" href="assets/dog.jpg">Sign up today</a></p>
        </div>
      </div>
    </div>
    <div class="carousel-item">
      <svg class="bd-placeholder-img" width="100%" height="100%" xmlns="" aria-hidden="true" preserveAspectRatio="xMidYMid slice" focusable="false"><rect width="100%" height="100%" fill="var(--bs-secondary-color)"/></svg>
      <div class="container">
        <div class="carousel-caption">
          <h1></h1>
          <p>Some representative placeholder content for the second slide of the carousel.</p>
          <p><a class="btn btn-lg btn-primary" href="#">Learn more</a></p>
        </div>
      </div>
    </div>
    <div class="carousel-item">
      <svg class="bd-placeholder-img" width="100%" height="100%" xmlns="http://www.w3.org/2000/svg" aria-hidden="true" preserveAspectRatio="xMidYMid slice" focusable="false"><rect width="100%" height="100%" fill="var(--bs-secondary-color)"/></svg>
      <div class="container">
        <div class="carousel-caption text-end">
          <h1>One more for good measure.</h1>
          <p>Some representative placeholder content for the third slide of this carousel.</p>
          <p><a class="btn btn-lg btn-primary" href="#">Browse gallery</a></p>
        </div>
      </div>
    </div>
  </div>
  <button class="carousel-control-prev" type="button" data-bs-target="#myCarousel" data-bs-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Previous</span>
  </button>
  <button class="carousel-control-next" type="button" data-bs-target="#myCarousel" data-bs-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Next</span>
  </button>
</div>
